import styled from "styled-components";

export const BtnMoreStyles = styled.button`
    background-color: #3498db;
    color: white;
    padding: 10px 20px;
    text-decoration: none;
    border-radius: 5px;
    font-size: 16px;
    transition: background-color 0.3s ease;

    &:hover {
        background-color: #2980b9;
    }
`
